// System applications for Web Systen 7

// Add menu items for System Applications
function GetMenuItems() { 
document.getElementById('m3').innerHTML += '<a href="javascript:createReadme();">Readme</a>';
document.getElementById('m3').innerHTML += '<a href="javascript:createCalc();">Calculator</a>';
document.getElementById('m3').innerHTML += '<a href="javascript:createBrowser();">Web Browser</a>';
document.getElementById('m3').innerHTML += '<a href="javascript:createConsole();">Console</a>';
}



// Calculator application for Web System 7

// Functions for math options
function multiply(){
a=Number(document.calculator.number1.value);
b=Number(document.calculator.number2.value);
c=a*b;
document.calculator.total.value=c;
}
function addition(){
a=Number(document.calculator.number1.value);
b=Number(document.calculator.number2.value);
c=a+b;
document.calculator.total.value=c;
}
function subtraction(){
a=Number(document.calculator.number1.value);
b=Number(document.calculator.number2.value);
c=a-b;
document.calculator.total.value=c;
}
function division(){
a=Number(document.calculator.number1.value);
b=Number(document.calculator.number2.value);
c=a/b;
document.calculator.total.value=c;
}
function modulus(){
a=Number(document.calculator.number1.value);
b=Number(document.calculator.number2.value);
c=a%b;
document.calculator.total.value=c;
}

// Drawing the Calculator window
function createCalc() { 
var divTag = document.createElement("div"); 
	divTag.id = "calc"; 
	divTag.className = "window"; 
            divTag.innerHTML = '<div id="wintop" onmousedown="dragStart(event, ' + "'calc'" + ')"><a href="javascript:CloseCalc();"><span id="close"></span></a><span>Calculator</span></div><div id="wincontent"><form name="calculator"><input type="text" name="number1" placeholder="1st number"><br /><input type="text" name="number2" placeholder="2nd number"><br /><input type="text" placeholder="Total" name="total">  <input type="button" value="ADD" onclick="javascript:addition();"><input type="button" value="SUB" onclick="javascript:subtraction();"><input type="button" value="MUL" onclick="javascript:multiply();"><input type="button" value="DIV" onclick="javascript:division();"><input type="button" value="MOD" onclick="javascript:modulus();"></form></div></div>'; 
            document.body.appendChild(divTag); 
} 
function CloseCalc(){var a=document.getElementById("calc");if(a){a.parentNode.removeChild(a)}}



