// Readme for Web System 7
function createReadme() { 
var divTag = document.createElement("div"); 
	divTag.id = "readme"; 
	divTag.className = "window"; 
            divTag.innerHTML = '<style>#readme{width:400px;}</style><div id="wintop" onmousedown="dragStart(event, ' + "'readme'" + ')"><a href="javascript:CloseReadme();"><span id="close"></span></a><span>Web System 7 Readme</span></div><div id="wincontent"><div style="height:274px;" id="scroll">Welcome to the Web System 7 Demo.<br /><br />What you are seeing is a digital re-creation of Macintosh System 7, which was at one point the flagship operating system for Mac computers, in a web browser. This demo is powered by CSS, HTML, and JavaScript. No JQuery, Flash, or other plugins are used to generate this. To begin, look at the Applications menu and select an app.<br /><br />Web System 7 allows the use of 3rd party applications using the SDK, which is still in development. You can launch the SDK alpha version by going to the JavaScript console in the Applications menu and typing <span style="color: red;">createSDK();</span> in it. You can also use the Console in the Applications menu to execute simple JavaScript commands.</div></div>'; 
            document.body.appendChild(divTag); 
}
function CloseReadme(){var a=document.getElementById("readme");if(a){a.parentNode.removeChild(a)}}



// JavaScript Console for Web System 7 (based on SDK compiler)

// Function for compiling code
function doIt() {
    var headID = document.getElementsByTagName("head")[0];
    var newScript = document.createElement("script");
    newScript.type = "text/javascript";
    newScript.innerHTML = document.getElementById("textarea").value;
    headID.appendChild(newScript);
}

// Drawing the window

function createConsole() { 
var divTag = document.createElement("div"); 
	divTag.id = "console"; 
	divTag.className = "window"; 
            divTag.innerHTML = '<div id="wintop" onmousedown="dragStart(event, ' + "'console'" + ')"><a href="javascript:CloseConsole();"><span id="close"></span></a><span>Console</span></div><div id="wincontent"><input type="text" name="textfield" size="40" placeholder="Type JavaScript command here" id="textarea" /><input type="button" value="Run" onclick="doIt();" /></div></div>'; 
            document.body.appendChild(divTag); 
}
function CloseConsole(){var a=document.getElementById("console");if(a){a.parentNode.removeChild(a)}}